
public class Ex2_1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// 멀티 컬럼 편집      	alt+shift+A, shift+방향키 상,하
		// 행 복사(여러 행 가능)	alt+ctrl+방향키 상,하
		// print()은 출력 후에 줄바꿈을 안함
		// println()은 출력 후에 줄바꿈을 함
		System.out.println("3+5");// ""안의 데이터는 문자열을 의미
		System.out.println(3+5);// 덧셈
		System.out.println(5-3);// 뺄셈
		System.out.println(5*3);// 곱셈
		System.out.println(4/2);// 나눗셈
	}

}
